mapper.select:
SELECT 

FROM a_farm a
WHERE a.farmId = #{farmId}

SELECT 

FROM a_farm a


		 AND (a.name LIKE concat('%',#{name},'%')
		 OR a.phone LIKE concat('%',#{name},'%'))	


		 AND a.openId = #{openId}


		 AND a.room = #{room}


		 AND a.farm = #{farm}


		 AND a.farm is not null


		 AND a.farm is null




		ORDER BY ${page.orderBy}


	    ORDER BY a.getTime DESC,a.farmId DESC

mapper.update:
UPDATE a_farm
openId = #{openId}, name = #{name}, phone = #{phone}, room = #{room}, farm = #{farm}, getTime = #{getTime}
WHERE farmId = #{farmId}
