mapper.select:
SELECT 

FROM a_questionnaire a
WHERE a.questionnaireId = #{questionnaireId}

mapper.update:
UPDATE a_questionnaire
room = #{room}, name = #{name}, inspector = #{inspector}, question1 = #{question1}, question2 = #{question2}, question3 = #{question3}, question4 = #{question4}, question5 = #{question5}, question6 = #{question6}, remark = #{remark}
WHERE questionnaireId = #{questionnaireId}
